import { Menu } from 'antd';
import { PureComponent } from 'react';
import {
    FallOutlined,
    MoneyCollectOutlined,
    RiseOutlined,
} from '@ant-design/icons';

const { SubMenu } = Menu;
export default class Sorter extends PureComponent {

    state = {
        current: 'mail',
    };

    handleClick = e => {
        console.log('click ', e.key);
        let active = e.key;
        this.setState({
            current: e.key,
        });

        if (active == "highlow") {
            this.applySort("HL");
            // this.props.callParent("Yes from sorter");
        } else if (active == "lowhigh") {

        } else if (active == "discount") {

        } else {
            console.log("Else")
        }
    };

    applySort = (type) => {
        // var items = [
        //     { name: 'Edward', value: 21 },
        //     { name: 'Sharpe', value: 37 },
        //     { name: 'And', value: 45 },
        //     { name: 'The', value: -12 },
        //     { name: 'Magnetic', value: 13 },
        //     { name: 'Zeros', value: 37 }
        // ];

        let items = [{ "name": "item 1", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 2", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 3", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 4", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 5", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 6", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 7", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 8", "price": { "actual": 319, "display": 900 }, "discount": 64 }]

        // sort by value
        // items.sort(function (a, b) {
        //     // return b.value - a.value;
        // });

        // let items = this.props.listData;
        items.sort(function (a, b) {
            return a.price.actual - b.price.actual;
        })

        console.log("Items sorted", items)

    }
    // if current key this.callParent



    render() {
        return (
            <div>
                <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
                    <Menu.Item key="sortby" disabled>
                        <span style={{ fontSize: "14px", fontWeight: "bolder" }}>Sort By  </span>
                    </Menu.Item>
                    <Menu.Item key="highlow">
                        <FallOutlined />
                        Price -- High Low
                    </Menu.Item>
                    <Menu.Item key="lowhigh">
                        <RiseOutlined />
                        Price -- Low High
                    </Menu.Item>
                    <Menu.Item key="discount">
                        <MoneyCollectOutlined />
                       Discount
                    </Menu.Item>
                </Menu>
            </div>
        );
    }
}
