import { PureComponent } from 'react';
import { Card, Col, Row, Button } from 'antd';

import actions from "../redux/action";
import { connect } from "react-redux";

const { onAddItemtoCart } = actions;

class ShoppingList extends PureComponent {

    constructor(props) {
        super(props);
    }

    addItemtoCart = (item, index) => {
        console.log("Item --> ", item)
        console.log("Index --> ", index)
        console.log("Index --> ", this.props.itemsInCart)


        this.props.onAddItemtoCart(item);
    }

    render() {
        console.log("shoppingListData", this.props.listData)
        let { listData } = this.props;
        return (
            <div>
                <div className="site-card-wrapper">
                    <Row gutter={16}>

                        {listData.map((item, index) =>
                            <Col span={4} className="listCols" key={index}>
                                <Card
                                    style={{ padding: "7px" }}
                                    cover={<img alt="example" src="https://cdn.britannica.com/s:700x500/77/170477-050-1C747EE3/Laptop-computer.jpg" />}
                                >

                                    <p>{item.name}</p>
                                    <p>
                                        <span> &#8377; {item.price.actual}</span>
                                        <span style={{ textDecoration: "line-through" }}> {item.price.display}</span>
                                        <span>  {item.discount} % off</span>
                                    </p>
                                    <Button type="primary" shape="round" size="middle" onClick={() => this.addItemtoCart(item, index)}>
                                        Add to Cart
                                    </Button>

                                </Card>



                            </Col>
                        )
                        }



                    </Row>
                </div>
            </div>
        );
    }
}



const mapStateToProps = state => {
    return {
        itemsInCart: state.itemsInCart
    };
};


export default connect(
    mapStateToProps, { onAddItemtoCart }
)(ShoppingList);
