const initialState = {
    shoppingListData: [],
    itemsInCart: []
};
const reducer = (state = initialState, action) => {
    const newState = { ...state };

    switch (action.type) {
        case "FETCH_ITEM_SUCCESS":
            console.log("Inside reducer", action.result)
            newState.shoppingListData = action.result.items
            break;

        case "GET_ITEMS_IN_CART":
            newState.itemsInCart;
            console.log("To get items in cart ", newState.itemsInCart)
            break;

        case "ADD_ITEM_TO_CART":
            console.log("Addinf more items ", action.payload)
            console.log("Addinf more items newState.itemsInCart", newState.itemsInCart)
            debugger;
            let temp = newState.itemsInCart;
            newState.itemsInCart = temp.push(action.payload)
            console.log("aFTER additng more items newState.itemsInCart", newState.itemsInCart)
    }
    return newState;
};






export default reducer;
