// import { delay } from "redux-saga";
import { call, all, put, takeEvery, takeLatest } from 'redux-saga/effects';


let headers = {
    'Content-Type': 'application/json'
};
export async function get(url, data) {
    //
    return new Promise(function (resolve, reject) {
        try {
            if (null === url || undefined === url || '' === url) {
                reject('URL not present.');
            } else {
                const token = getCookie('id_token');
                if (undefined !== token) {
                    headers.Authorization = 'Bearer ' + token;
                }
                const options = {
                    credentials: 'include', // Needed to set the cookies in few browsers (Safari)
                    method: 'GET',
                    headers: headers,
                    // body: JSON.stringify(data)
                };
                fetch(url, options)
                    .then(res => {
                        if (200 === res.status) {
                            return res.json();
                        } else {
                            const error = { response: res };
                            _validateError(error, resolve, reject);
                        }
                    })
                    .then(res => {
                        resolve(res);
                    });
            }
        } catch (error) {
            _validateError(error, resolve, reject);
        }
    });
}


function* onFetchItem() {
    // yield delay(4000);
    console.log("Inside sAga On Fethc Item")

    // yield put({ type: "DELETE_ITEM_SUCCESS", value: 1 });
    let res = localStorage.getItem("parentReleaseData")
    yield put({ type: "FETCH_ITEM_SUCCESS", result: res });
}

// export function* watchAgeUp() {
//     yield takeLatest("AGE_UP", ageUpAsync);
// }


export default function* rootSaga() {
    yield takeEvery("FETCH_ITEM", onFetchItem);
}
