import { Component } from "react";

import Filter from "../components/Filter";
import Sorter from "../components/Sorter";
import ShoppingList from "../components/ShoppingList";

import { List, Avatar, Row, Col, Card } from 'antd';

import actions from "../redux/action";
import { connect } from "react-redux";

const { onFetchItem } = actions;

const jsondata = [{ "name": "item 1", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 2", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 3", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 4", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 5", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 6", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 7", "price": { "actual": 319, "display": 900 }, "discount": 64 }, { "name": "item 8", "price": { "actual": 319, "display": 900 }, "discount": 64 }]

class ListPage extends Component {

    constructor(props) {
        super(props);
        this.state = {}
    }

    componentDidMount() {
        // fetchList
        this.props.onFetchItem();
    }

    render() {
        console.log("props data", this.props.shoppingListData)
        return (
            <>
                <div className="cartContainerDiv">
                    <div className="cartLeftNav">
                        <div>
                            {jsondata.map((item, index) =>
                                <Row gutter={16} key={index}>
                                    <Col span={6}>
                                        <div className="cartListDiv">
                                            <img
                                                alt="example"
                                                src="https://cdn.britannica.com/s:700x500/77/170477-050-1C747EE3/Laptop-computer.jpg"
                                            />
                                        </div>
                                    </Col>
                                    <Col span={6}>
                                        <div className="cartListText">
                                            {item.name}
                                        </div>
                                    </Col>
                                    <Col span={6}>
                                        <div className="cartListText">
                                            {item.price.actual}
                                        </div>
                                    </Col>
                                    <Col span={6}>
                                        <div className="cartListText">
                                            {item.discount}
                                        </div>
                                    </Col>
                                </Row>
                            )}

                        </div>
                    </div>
                    <div className="cartRightNav">
                        <Card title="Price Details" style={{ width: 300 }}>
                            {/* <p>Price --no of items -- </p>
                            <p>discount</p>
                            <p>Total Payable</p> */}
                            <Row gutter={16}>
                                <Col span={16}>
                                    Price
                                </Col>
                                <Col span={8}>
                                    Amount
                                </Col>
                            </Row>
                            <Row gutter={16}>
                                <Col span={16}>
                                    Discount
                                </Col>
                                <Col span={8}>
                                    Amount
                                </Col>
                            </Row>
                            <hr />
                            <Row gutter={16}>
                                <Col span={16}>
                                    Total
                                </Col>
                                <Col span={8}>
                                    Amount
                                </Col>
                            </Row>

                        </Card>
                    </div>

                    {/* <div className="leftNav">
                        <Filter />
                    </div>
                    <div className="mainComponent">
                        <Sorter />
                        <ShoppingList listData={this.props.shoppingListData} />
                    </div> */}
                </div>
            </>
        )
    }
}


const mapStateToProps = state => {
    return {
        shoppingListData: state.shoppingListData
    };
};


export default connect(
    mapStateToProps, { onFetchItem }
)(ListPage);
